const { createDocument } = require('../test-utils');
const { defaultHeaders } = require('../index');

describe('Education section', () => {
  it('is rendered if present', () => {
    const doc = createDocument({
      "education": [
        {
          "institution": "Life",
          "area": "Smurf Village",
          "startDate": "1969-06-23",
          "endDate": "2469-06-23"
        }
      ]
    });
    var edu = doc.querySelectorAll('#education ul li');
    expect(edu.length).toBe(1);
    expect(edu[0].querySelector('h4').textContent).toBe('Life');
    expect(edu[0].querySelector('.caption').textContent).toContain('Smurf Village');
    expect(edu[0].querySelector('.caption').textContent).toContain('1969');
    expect(edu[0].querySelector('.caption').textContent).toContain('2469');
  });

  it('is not rendered if empty', () => {
    const doc = createDocument({
      "education": []
    });
    expect(doc.querySelector('#education')).toBeNull();
  });

  it('is not rendered if missing', () => {
    const doc = createDocument();
    expect(doc.querySelector('#education')).toBeNull();
  });

  describe('Header', () => {
    it('has default value', () => {
      const doc = createDocument({
        "education": [
          {
            "institution": "Life",
            "area": "Smurf Village",
            "startDate": "1969-06-23",
            "endDate": "2469-06-23"
          }
        ]
      });
      expect(doc.querySelector('#education h2').textContent).toBe(defaultHeaders.education);
    });

    it('supports custom value', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": "Training"
          }
        },
        "education": [
          {
            "institution": "Life",
            "area": "Smurf Village",
            "startDate": "1969-06-23",
            "endDate": "2469-06-23"
          }
        ]
      });
      expect(doc.querySelector('#education h2').textContent).toBe('Training');
    });

    it('is not rendered if custom value is set to false', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": false
          }
        },
        "education": [
          {
            "institution": "Life",
            "area": "Smurf Village",
            "startDate": "1969-06-23",
            "endDate": "2469-06-23"
          }
        ]
      });
      expect(doc.querySelector('#education h2')).toBeNull();
      expect(doc.querySelectorAll("#education ul li").length).toBe(1);
    });
    
    it('is not rendered if education section is empty', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": "Training"
          }
        },
        "education": []
      });
      expect(doc.querySelector('#education h2')).toBeNull();
    });

    it('is not rendered if education section is missing', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": "Training"
          }
        }
      });
      expect(doc.querySelector('#education h2')).toBeNull();
    });
  });

  describe('Footer link', () => {
    it('supports default header', () => {
      const doc = createDocument({
        "education": [
          {
            "institution": "Life",
            "area": "Smurf Village",
            "startDate": "1969-06-23",
            "endDate": "2469-06-23"
          }
        ]
      });
      expect(doc.querySelector('footer a[href="#education"]').textContent).toBe(defaultHeaders.education);
    });

    it('supports custom header', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": "Training"
          }
        },
        "education": [
          {
            "institution": "Life",
            "area": "Smurf Village",
            "startDate": "1969-06-23",
            "endDate": "2469-06-23"
          }
        ]
      });
      expect(doc.querySelector('footer a[href="#education"]').textContent).toBe('Training');
    });

    it('is not rendered if custom header is set to false', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": false
          }
        },
        "education": [
          {
            "institution": "Life",
            "area": "Smurf Village",
            "startDate": "1969-06-23",
            "endDate": "2469-06-23"
          }
        ]
      });
      expect(doc.querySelector('footer a[href="#education"]')).toBeNull();
    });

    it('is not rendered if education section is empty', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": "Training"
          }
        },
        "education": []
      });
      expect(doc.querySelector('footer a[href="#education"]')).toBeNull();
    });

    it('is not rendered if education section is missing', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "education": "Training"
          }
        }
      });
      expect(doc.querySelector('footer a[href="#education"]')).toBeNull();
    });
  });
});
