const { createDocument } = require('../test-utils');
const { defaultHeaders } = require('../index');

describe('Publications section', () => {
  it('is rendered if present', () => {
    const doc = createDocument({
      "publications": [
        {
          "name": "Smurf Village Daily",
          "publisher": "Papa Smurf",
          "releaseDate": "2021-06-23"
        }
      ]
    });
    const publications = doc.querySelectorAll('#publications ul li');
    expect(publications.length).toBe(1);
    expect(publications[0].querySelector('h4').textContent).toBe('Smurf Village Daily');
    expect(publications[0].querySelector('.caption').textContent).toContain('Papa Smurf');
    expect(publications[0].querySelector('.caption').textContent).toContain('2021');
  });

  it('is not rendered if empty', () => {
    const doc = createDocument({
      "publications": []
    });
    expect(doc.querySelector('#publications')).toBeNull();
  });

  it('is not rendered if missing', () => {
    const doc = createDocument();
    expect(doc.querySelector('#publications')).toBeNull();
  });

  describe('Header', () => {
    it('has default value', () => {
      const doc = createDocument({
        "publications": [{ "name": "Smurf Village Daily" }]
      });
      expect(doc.querySelector('#publications h2').textContent).toBe(defaultHeaders.publications);
      expect(doc.querySelectorAll('#publications li').length).toBe(1);
    });

    it('supports custom value', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": "Custom Publications Header"
          }
        },
        "publications": [{ "name": "Smurf Village Daily" }]
      });
      expect(doc.querySelector('#publications h2').textContent).toBe('Custom Publications Header');
      expect(doc.querySelectorAll('#publications li').length).toBe(1);
    });

    it('is not rendered if custom value is set to false', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": false
          }
        },
        "publications": [{ "name": "Smurf Village Daily" }]
      });
      expect(doc.querySelector('#publications h2')).toBeNull();
      expect(doc.querySelectorAll('#publications li').length).toBe(1);
    });

    it('is not rendered if publications section is empty', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": "Publications"
          }
        },
        "publications": []
      });
      expect(doc.querySelector('#publications h2')).toBeNull();
    });

    it('is not rendered if publications section is missing', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": "Publications"
          }
        }
      });
      expect(doc.querySelector('#publications h2')).toBeNull();
    });
  });

  describe('Footer link', () => {
    it('supports default header', () => {
      const doc = createDocument({
        "publications": [{ "name": "Smurf Village Daily" }]
      });
      expect(doc.querySelector('footer a[href="#publications"]').textContent).toBe(defaultHeaders.publications);
    });

    it('supports custom header', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": "Smurfications"
          }
        },
        "publications": [{ "name": "Smurf Village Daily" }]
      });
      expect(doc.querySelector('footer a[href="#publications"]').textContent).toBe('Smurfications');
    });

    it('is not rendered if custom header is set to false', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": false
          }
        },
        "publications": [{ "name": "Smurf Village Daily" }]
      });
      expect(doc.querySelector('footer a[href="#publications"]')).toBeNull();
    });

    it('is not rendered if publications section is empty', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": "Smurfications"
          }
        },
        "publications": []
      });
      expect(doc.querySelector('footer a[href="#publications"]')).toBeNull();
    });

    it('is not rendered if publications section is missing', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "publications": "Smurfications"
          }
        }
      });
      expect(doc.querySelector('footer a[href="#publications"]')).toBeNull();
    });
  });
});