const { createDocument } = require('../test-utils');
const { defaultHeaders } = require('../index');

describe('Volunteer section', () => {
  it('is rendered if present', () => {
    const doc = createDocument({
      "volunteer": [
        {
          "summary": "Antidote",
          "position": "Wizard",
          "startDate": "1969-06-23",
          "endDate": "2469-06-23",
          "highlights": [
            "Saved the world"
          ]
        }
      ]
    });
    const volunteer = doc.querySelectorAll('#volunteer > div > ul > li');
    expect(volunteer.length).toBe(1);
    expect(volunteer[0].querySelector('h4').textContent).toBe('Antidote');
    expect(volunteer[0].querySelector('.caption span').textContent).toBe('Wizard');
    expect(volunteer[0].querySelectorAll('.caption time')[0].textContent).toContain('1969');
    expect(volunteer[0].querySelectorAll('.caption time')[1].textContent).toContain('2469');
    const highlights = volunteer[0].querySelectorAll('li');
    expect(highlights.length).toBe(1);
    expect(highlights[0].textContent).toBe('Saved the world');
  });

  it('is not rendered if empty', () => {
    const doc = createDocument({
      "volunteer": []
    });
    expect(doc.querySelector('#volunteer')).toBeNull();
  });

  it('is not rendered if missing', () => {
    const doc = createDocument();
    expect(doc.querySelector('#volunteer')).toBeNull();
  });

  describe('Header', () => {
    it('has default value', () => {
      const doc = createDocument({
        "volunteer": [
          {
            "summary": "Antidote",
            "position": "Wizard",
            "highlights": [
              "Saved the world"
            ]
          }
        ]
      });
      expect(doc.querySelector('#volunteer h2').textContent).toBe(defaultHeaders.volunteer);
    });

    it('supports custom value', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": "Custom Volunteer Header"
          }
        },
        "volunteer": [
          {
            "summary": "Antidote",
            "position": "Wizard",
            "highlights": [
              "Saved the world"
            ]
          }
        ]
      });
      expect(doc.querySelector('#volunteer h2').textContent).toBe('Custom Volunteer Header');
    });

    it('is not rendered if custom value is set to false', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": false
          }
        },
        "volunteer": [
          {
            "summary": "Antidote",
            "position": "Wizard",
            "highlights": [
              "Saved the world"
            ]
          }
        ]
      });
      expect(doc.querySelector('#volunteer h2')).toBeNull();
      expect(doc.querySelectorAll('#volunteer > div > ul > li').length).toBe(1);
    });

    it('is not rendered if volunteer section is empty', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": "Volunteering"
          }
        },
        "volunteer": []
      });
      expect(doc.querySelector('#volunteer h2')).toBeNull();
    });

    it('is not rendered if volunteer section is missing', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": "Volunteering"
          }
        }
      });
      expect(doc.querySelector('#volunteer h2')).toBeNull();
    });
  });

  describe('Footer link', () => {
    it('supports default header', () => {
      const doc = createDocument({
        "volunteer": [
          {
            "summary": "Antidote",
            "position": "Wizard",
            "highlights": [
              "Saved the world"
            ]
          }
        ]
      });
      expect(doc.querySelector('footer a[href="#volunteer"]').textContent).toBe(defaultHeaders.volunteer);
    });

    it('supports custom header', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": "Custom Volunteer Header"
          }
        },
        "volunteer": [
          {
            "summary": "Antidote",
            "position": "Wizard",
            "highlights": [
              "Saved the world"
            ]
          }
        ]
      });
      expect(doc.querySelector('footer a[href="#volunteer"]').textContent).toBe('Custom Volunteer Header');
    });

    it('is not rendered if custom header is set to false', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": false
          }
        },
        "volunteer": [
          {
            "summary": "Antidote",
            "position": "Wizard",
            "highlights": [
              "Saved the world"
            ]
          }
        ]
      });
      expect(doc.querySelector('footer a[href="#volunteer"]')).toBeNull();
    });

    it('is not rendered if volunteer section is empty', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": "Volunteering"
          }
        },
        "volunteer": []
      });
      expect(doc.querySelector('footer a[href="#volunteer"]')).toBeNull();
    });

    it('is not rendered if volunteer section is missing', () => {
      const doc = createDocument({
        "meta": {
          "headers": {
            "volunteer": "Volunteering"
          }
        }
      });
      expect(doc.querySelector('footer a[href="#volunteer"]')).toBeNull();
    });
  });
});